import { useState, useEffect } from "react"

export const ArrayMtds = () => {
    const [status, setstatus] = useState(1)
    useEffect(() => {
        let arr = [{
            "name": "kumar",
            "age": 20,
            "mail": "kumarmadhu@gmail.com"
        }, {
            "name": "mamatha",
            "age": 30,
            "mailId": "kumarmadhu@gmail.com"
        }]
        //Filter method(status=1).
        if (status === 1) {
            let word = 20
            let newArr = arr.filter((res, i) => {
                // By using Object.keys we can take the keys of an object and by using Object.values we can take 
                // the values of an object
                for (var key in Object.values(res)) {
                    if (Object.values(res)[key].toString().includes(word))
                        return true
                }
                return false
            })
            console.log(newArr)
        }
        //ForEach method.
        else if (status === 2) {
            arr.forEach((res, i, array) => {
                console.log(res, array)
            })
        }
    }, [])

    return (
        <div>
            kumar Madhu working
        </div>
    )
}
