import React, { useEffect, useState } from "react";
import "../css/loginPage.css";
import { connect } from 'react-redux';
import { GetList } from '../actions/loginPageActions';

const LoginPage = (props) => {
    const [checked, setChecked] = useState(false)
    useEffect(() => {
        let users = [{
            "username": "hruday@gmail.com",
            "password": 'hruday123'
        }]
        props.GetList(users)
    }, [])

    return (
        <div className="login_container">
            <div className="login_page">
                <div className="main_txt">Login</div>
                <div className="userInput_field">
                    <i className="glyphicon glyphicon-user user_icon" />
                    <input className="input_field" type="text" placeholder="Username" />
                    <div className="err_msg">Not a valid User!</div>
                </div>
                <div className="userInput_field">
                    <i className="glyphicon glyphicon-lock user_icon" />
                    <input className="input_field" type={checked == true ? "text" : "password"} placeholder="Password" />
                    <div className="show_pwd_con">
                        <input type="checkbox" checked={checked} onClick={() => { setChecked(!checked) }} />
                        <div className="pwd_txt" onClick={() => { setChecked(!checked) }}>Show Password</div>
                    </div>
                    {/* <div className="err_msg">Not a valid User!</div> */}
                </div>
                <button>Login</button>
            </div>
        </div>
    )
}

const mapStateToProps = state => ({
    userList: state.userList
})

const mapDispatchToProps = dispatch => ({
    GetList: (data) => dispatch(GetList(data))
})

export default connect(mapStateToProps, mapDispatchToProps)(LoginPage);
// export default LoginPage;
