import { useState, useEffect } from "react"

export const ArrayMtds = () => {
    const [status, setstatus] = useState(4)
    useEffect(() => {
        //If we use the negative value for index, the value is counting from backend of the array.
        let arr1 = [5,12,8,130,144,10]
        let arr = [{
            "name": "kumar",
            "age": 20,
            "mail": "kumarmadhu@gmail.com"
        }, {
            "name": "mamatha",
            "age": 30,
            "mailId": "kumarmadhu@gmail.com"
        }]
        //Filter method(status=1).
        if (status === 1) {
            let word = 20
            let newArr = arr.filter((res) => {
                // By using Object.keys we can take the keys of an object and by using Object.values we can take 
                // the values of an object.
                for (var key in Object.values(res)) {
                    if (Object.values(res)[key].toString().includes(word.toString().toLowerCase()))
                        return true
                }
                return false
            })
            console.log(newArr)
        }
        // Difference b/w ForEach and map.
        // * Both forEach and map recieves a function as an argument and execute it once for each array Element
        //   but forEach return undefined value and map return new altered array.
        // * we can't chain other methods in forEach like reduce, filter and reduce method where in map method we can.
        // * The original array will change if we are using forEach method where as in map it return completely new array.

        //ForEach method.
        else if (status === 2) {
            let newArr = arr1.forEach((res, i) => {
                return res.age*5
                // return arr[i] = res.age*5
            })
            console.log(newArr,arr1)
        }

        //map method.
        else if (status === 3) {
            let newArr = arr1.map(res=>{
                return res.age*5
            })
            console.log(newArr, arr1)
        }

        //at method,
        else if (status === 4){
            // let id = arr1.at(2)
            console.log(arr.at(2), "index value 2")
            // console.log(arr1.at(-3), arr1, "index value -3")
            // console.log(arr1.at(7), arr1, "index value 7")
        }
    }, [])

    return (
        <div>
            kumar Madhu
            Arry methods
        </div>
    )
}
